const swiper=new Swiper(".swiper",{direction:"horizontal",loop:!0,autoplay:{delay:5e3,disableOnInteraction:!1}});let files=[];const fileLabel=document.querySelector(".form__file-label"),inputFile=document.querySelector(".form__input-file"),screenshotWrap=document.createElement("div");screenshotWrap.classList.add("form__screenshot-wrap"),fileLabel.insertAdjacentElement("beforeend",screenshotWrap);const changeHandler=e=>{e.target.files.length&&(files=Array.from(e.target.files),files.forEach((e=>{if(!e.type.match("image"))return;const a=new FileReader;a.onload=a=>{const t=a.target.result;screenshotWrap.insertAdjacentHTML("afterbegin",`\n          <div class="form__preview-image">\n            <div class="form__image-remove" data-name="${e.name}">&times;</div>\n            <img class='form__file-image' src="${t}" alt="${e.name}" />\n          </div>\n        `)},a.readAsDataURL(e)})))},removeHandler=e=>{if(e.preventDefault(),!e.target.dataset.name)return;const{name:a}=e.target.dataset;files=files.filter((e=>e.name!==a));screenshotWrap.querySelector(`[data-name="${a}"]`).closest(".form__preview-image").remove(),inputFile.value=""};fileLabel.addEventListener("change",changeHandler),screenshotWrap.addEventListener("click",removeHandler);